CREATE TABLE `malala`.`posts` ( `post_id` INT(10) NOT NULL AUTO_INCREMENT , `post_title` VARCHAR(100) NOT NULL , `post_date` DATE NOT NULL , `post_author` VARCHAR(100) NOT NULL , `post_image` TEXT NOT NULL , `post_keywords` TEXT NOT NULL , `post_content` TEXT NOT NULL , PRIMARY KEY (`post_id`)) ENGINE = InnoDB;